(function(){tinymce.PluginManager.requireLangPack("gtranslator");tinymce.create("tinymce.plugins.GtranslatorPlugin",{init:function(ed,url){ed.addCommand("mceGtranslator",function(){ed.windowManager.open({file:url+"/dialog.htm",width:640,height:320,inline:1},{plugin_url:url})});ed.addButton("gtranslator",{title:"gtranslator.desc",cmd:"mceGtranslator",image:url+"/img/gtranslator.gif"});ed.onNodeChange.add(function(ed,cm,n,co){var selection=ed.selection.getContent({format:"html"});cm.setDisabled("gtranslator",selection.length<=0)})},createControl:function(n,cm){return null},getInfo:function(){return{longname:"Google translator plugin for TinyMCE",author:"vifito",authorurl:"http://openhost.es",infourl:"http://openhost.es/",version:"1.0"}}});tinymce.PluginManager.add("gtranslator",tinymce.plugins.GtranslatorPlugin)})();