(function(){tinymce.PluginManager.requireLangPack("xornal");tinymce.create("tinymce.plugins.XornalPlugin",{init:function(a,b){a.addCommand("mceXornalAutoLink",function(){var c=tinyMCE.activeEditor.getContent();tinymce.util.XHR.send({url:"/admin/pclave.php?action=autolink",type:"POST",data:tinymce.util.JSON.serialize({content:c}),success:function(d){tinyMCE.activeEditor.setContent(d);}});});a.addButton("xornalautolink",{title:"Enlazar palabras clave",cmd:"mceXornalAutoLink",image:b+"/img/database_link.png"});},getInfo:function(){return{longname:"Xornal.com plugin",author:"Openhost S.L.",authorurl:"http://openhost.es",infourl:"http://redmine.openhost.es",version:"1.0"};}});tinymce.PluginManager.add("xornal",tinymce.plugins.XornalPlugin);})();